  // const toggleTheme = () => {
  //   if (theme === 'light') {
  //     setTheme(dark);
  //   } else {
  //     setTheme(light);
  //   }
  // }
  return (
    <div style={{ '--theme_base_color': theme.base_color, '--theme_para_text_color': theme.para_text_color, '--theme_color': theme.color }}>
      <div className="flex">
        <div className="w-64 bg-gray-100 h-screen">
          <Navbar />
        </div>
        <div className="w-3/4 bg-white">
          <Intro />
          <Content />
        </div>
      </div>
    </div>
  );

}

export default App;
