import Content from "./components/Content";
import Intro from "./components/Intro";
import Navbar from "./components/Navbar";
import React, { useState } from 'react';

function App() {
  const dark = { base_color: '#0F172A', para_text_color: '#899BB5', color: '#52CDCF' } //dark
  const light = { base_color: '#9ED8DB', para_text_color: 'black', theme_color: '#E8F1F2' } //light
  var theme = {}
  theme = dark;
  // const [theme, setTheme] = useState(dark)
  const toggleTheme = () => {
    if (theme === light) {
      theme = dark;
    }
    else {
      theme = light;
    }

  }
  // const [theme, setTheme] = useState('light');
  // const theme = { base_color: '#0F172A', para_text_color: '#899BB5', color: '#52CDCF' }
  // const toggleTheme = () => {
  //   if (theme === 'light') {
  //     setTheme(dark);
  //   } else {
  //     setTheme(light);
  //   }
  // }
  return (
    <div className="flex">
      <div className="w-64 bg-gray-100 h-screen">
        <Navbar theme={theme} />
      </div>
      <div className="w-3/4 bg-white">
        <Intro theme={theme} />
        <Content theme={theme} />
      </div>
    </div>
  );

}

export default App;
