import React from 'react'
import { darkTheme } from '../data/theme'
import { useState } from "react";

const ToggleTheme = () => {
    const [enabled, setEnabled] = useState(false);
    return (
        <div style={{ '--theme_base_color': darkTheme.base_color, '--theme_para_text_color': darkTheme.para_text_color, '--theme_color': darkTheme.color }}>
            <div
                className="md:w-14 md:h-7 w-12 h-6 flex items-center bg-gray rounded-full p-1 cursor-pointer"
            >
                {/* Switch */}
                <div
                    className="bg-white md:w-6 md:h-6 h-5 w-5 rounded-full shadow-md"
                ></div>
            </div>
        </div>


    )
}

export default ToggleTheme
