import React from 'react'
import { useState } from "react"
import light from 'C:/Users/rrajo/OneDrive/Desktop/Coursera/portfolio-repo/my-project/src/App.js' 
import dark from 'C:/Users/rrajo/OneDrive/Desktop/Coursera/portfolio-repo/my-project/src/App.js'

const ToggleTheme = (prop) => {
    // const dark = { base_color: '#0F172A', para_text_color: '#899BB5', color: '#52CDCF' } //dark
    // const light = { base_color: '#9ED8DB', para_text_color: 'black', theme_color: '#E8F1F2' } //light

    const [theme, setTheme] = useState(dark)
    const toggleTheme = () => {
        if (theme === light) {
            setTheme(dark);
        }
        else {
            setTheme(light);
        }
        
    }

    return (
        <>
            <button onClick={toggleTheme}>ToggleTheme</button>
        </>
    )
}

export default ToggleTheme
