import Content from "./components/Content";
import Intro from "./components/Intro";
import Navbar from "./components/Navbar";
import React, { useState } from 'react';

function App() {
  // const [theme, setTheme] = useState('light');
  return (
    <div style={{ '--theme_base_color': theme.base_color, '--theme_para_text_color': theme.para_text_color, '--theme_color': theme.color }}>
      <div className="flex">
        <div className="w-64 bg-gray-100 h-screen">
          <Navbar />
        </div>
        <div className="w-3/4 bg-white">
          <Intro />
          <Content />
        </div>
      </div>
    </div>
  );

}

export default App;
export function toggleTheme() {
  // const [theme, setTheme] = useState('light');
  // // const theme = { base_color: '#0F172A', para_text_color: '#899BB5', color: '#52CDCF' }
  {var theme = {}};
  const dark = { base_color: '#0F172A', para_text_color: '#899BB5', color: '#52CDCF' }
  const light = { base_color: 'white', para_text_color: 'black', theme_color: '#209799' }
  if (theme === 'light') {
    {theme=dark};
  } else {
    {theme=light};
  }
}
