import Content from "./components/Content";
import Intro from "./components/Intro";
import Navbar from "./components/Navbar";
import React, { useState } from 'react';

function App() {
  const [theme, setTheme] = useState('light');
  const dark = { base_color: '#0F172A', para_text_color: '#899BB5', theme_color: '#52CDCF' }
  const light = { base_color: 'black', para_text_color: 'black', theme_color: '#209799' }

  const toggleTheme = () => {
    if (theme === 'light') {
      setTheme(dark);
    } else {
      setTheme(light);
    }
  }
  return (
    <div style={{ '--theme_base_color': theme.base_color }}>
      <div className="flex">
        <div className="w-64 bg-gray-100 h-screen">
          <Navbar />
        </div>
        <div className="w-3/4 bg-white">
          <Intro />
          <Content />
        </div>
      </div>
    </div>
  );

}

export default App;
